import axios from "axios";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import { url } from "../../../Commons/constants";
import { validatePackag } from "@progress/kendo-react-common";

const SignUp = () => {
  const [username, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [gender, setGender] = useState("");
  const [phone, setPhone] = useState("");
  const [dob, setdob] = useState("");
  const [address, setAddress] = useState("");
  const [role, setRole] = useState("user");

  // get the history object
  const history = useHistory();

  const validateData = () => {
    const formData = {
      username,
      email,
      password,
      gender,
      phone,
      dob,
      address,
      role,
    };

    const usernameValidator = validatePackag(formData.username, [
      { required: true },
      { minLength: 3 },
      { maxLength: 20 },
    ]);

    const emailValidator = validatePackag(formData.email, [
      { required: true },
      { email: true },
      { maxLength: 50 },
    ]);

    const passwordValidator = validatePackag(formData.password, [
      { required: true },
      { minLength: 8 },
      { maxLength: 20 },
    ]);

    const genderValidator = validatePackag(formData.gender, [{ required: true }]);

    const phoneValidator = validatePackag(formData.phone, [
      { required: true },
      { pattern: "^\\d{10}$" },
    ]);

    const dobValidator = validatePackag(formData.dob, [{ required: true }]);

    const addressValidator = validatePackag(formData.address, [
      { required: true },
      { maxLength: 100 },
    ]);

    const roleValidator = validatePackag(formData.role, [{ required: true }]);

    return {
      username: usernameValidator,
      email: emailValidator,
      password: passwordValidator,
      gender: genderValidator,
      phone: phoneValidator,
      dob: dobValidator,
      address: addressValidator,
      role: roleValidator,
    };
  };

  // gets called when user selects an image
  // const onFileSelect = (event) => {
  // setThumbnail(event.target.files[0])

  const addUserToDB = () => {
    const validationResult = validateData();

    if (
      !validationResult.username.isValid ||
      !validationResult.email.isValid ||
      !validationResult.password.isValid ||
      !validationResult.gender.isValid ||
      !validationResult.phone.isValid ||
      !validationResult.dob.isValid ||
      !validationResult.address.isValid ||
      !validationResult.role.isValid
    ) {
      alert("Please fill all the required fields correctly");
      return;
    } else {
      const data = new FormData();
      // add the data
      data.append("username", username);
      data.append("email", email);
      data.append("password", password);
      data.append("gender", gender);
      data.append("phone", phone);
      data.append("dob", dob);
      data.append("address", address);
      data.append("role", role);

      // send the data to the API
      axios.post(url + "/user/addUser", data).then((response) => {
        const result = response.data;
        if (result.status === "success") {
          alert("Sign Up Successfully");

          history.push("/signin");
        } else {
          alert("Error while Sign Up");
        }
      });
    }
  };

  return (
    <div style={{ display: "flex" }}>
      <div
        className="container"
        style={{ width: "50%", display: "block" }}
      >
        <h1 className="title-header">Sign Up</h1>
        <div className="mb-3">
          <label htmlFor="">User Name*</label>
          <input
            onChange={(e) => {
              setUserName(e.target.value);
            }}
            type="text"
            required
            className="form-control"
          />
          {!validateData().username.isValid && (
            <p style={{ color: "red" }}>
              {validateData().username.errorMessages[0]}
            </p>
          )}
        </div>
        <div className="mb-3">
          <label htmlFor="">Email*</label>
          <input
            onChange={(e) => {
              setEmail(e.target.value);
            }}
            type="email"
            required
            className="form-control"
          />
          {!validateData().email.isValid && (
            <p style={{ color: "red" }}>
              {validateData().email.errorMessages[0]}
            </p>
          )}
        </div>

        <div className="mb-3">
          <label htmlFor="">Password*</label>
          <input
            onChange={(e) => {
              setPassword(e.target.value);
            }}
            type="password"
            required
            className="form-control"
          />
          {!validateData().password.isValid && (
            <p style={{ color: "red" }}>
              {validateData().password.errorMessages[0]}
            </p>
          )}
        </div>

        <label htmlFor="">Gender*</label>
        <br />

        <div
          class="form-check"
          onChange={(e) => {
            setGender(e.target.value);
          }}
        >
          <input type="radio" value="Male" name="gender" /> Male
          <br />
          <input type="radio" value="Female" name="gender" /> Female
          <br />
          <input type="radio" value="Other" name="gender" /> Other
          <br />
          {!validateData().gender.isValid && (
            <p style={{ color: "red" }}>
              {validateData().gender.errorMessages[0]}
            </p>
          )}
        </div>
        <div className="mb-3">
          <label htmlFor="">Phone*</label>
          <input
            onChange={(e) => {
              setPhone(e.target.value);
            }}
            type="number"
            required
            className="form-control"
          />
          {!validateData().phone.isValid && (
            <p style={{ color: "red" }}>
              {validateData().phone.errorMessages[0]}
            </p>
          )}
        </div>

        <div className="mb-3">
          <label htmlFor="">DOB*</label>
          <input
            onChange={(e) => {
              setdob(e.target.value);
            }}
            type="date"
            required
            defaultValue="2022-01-01"
            InputLabelProps={{
              shrink: true,
            }}
            className="form-control"
          />
          {!validateData().dob.isValid && (
            <p style={{ color: "red" }}>
              {validateData().dob.errorMessages[0]}
            </p>
          )}
        </div>

        <div className="mb-3">
          <label htmlFor="">Address*</label>
          <input
            onChange={(e) => {
              setAddress(e.target.value);
            }}
            type="text"
            required
            className="form-control"
          />
          {!validateData().address.isValid && (
            <p style={{ color: "red" }}>
              {}
            </p>
          )}
        </div>
        <div className="mb-3">
          <label htmlFor="">role*</label>
          <input
            onChange={(e) => {
              setRole(e.target.value);
            }}
            type="text"
            required
            className="form-control"
          />
          {!validateData().role.isValid && (
            <p style={{ color: "red" }}>
              {validateData().role.errorMessages[0]}
            </p>
          )}
        </div>

        <div className="mb-3">
          <button
            onClick={addUserToDB}
            className="col-2 btn btn-sm btn-success"
          >
            Add
          </button>
          &nbsp;&nbsp;
          <button
            onClick={history.goBack}
            className="col-2 btn  btn-sm btn-warning"
          >
            Back
          </button>
        </div>
      </div>
    </div>
  );
};
export default SignUp;
